<script>
    $(document).ready(function(event) {
        $('#successmessage').hide();
        $('#fupForm').on('submit', function(event) {
            event.preventDefault();
            var btn = document.getElementById('submit_button');
            btn.disabled = true;
            var branch = $('#branh_id').val();
            $.ajax({
                type: 'POST',
                url: 'email.php',
                data: new FormData(this),
                contentType: false,
                cache: false,
                processData: false,
                success: function(data) {
                    // alert(data);
                    $('#successmessage').show();
                    $('.successmessagetext').html(data);
                    // window.location.reload();
                    btn.disabled = false;
                    $("#fupForm")[0].reset();

                }
            });
        });


        $('.mobileToggle').click(function() {
            $('.collapsedbox').toggleClass('mobile_active_class');
        })

        $('.campusupdate_hide_item0').hide();
        $('.campusupdate_hide_item1').hide();
        $('.campusupdate_hide_item_2').hide();
        $('.campusupdate_hide_item3').hide();
        $('.campusupdate_hide_item4').hide();
        $('.campusupdate_hide_item5').hide();


        $('.placement_hide_item').hide();


        $('.viewbutton').click(function() {
            var teams = $(".viewbutton").html();
            $('.placement_hide_item').slideToggle();


            if (teams == "View More") {
                $(".viewbutton").html("View Less");
            } else {
                $(".viewbutton").html("View More");
            }
        })

        $('.readmore_button0').click(function() {
            var teams = $(".readmore_button0").html();
            $('.campusupdate_hide_item0').slideToggle();


            if (teams == "Read more") {
                $(".readmore_button0").html("Read less");
            } else {
                $(".readmore_button0").html("Read more");
            }
        })
        $('.readmore_button1').click(function() {
            var teams = $(".readmore_button1").html();
            $('.campusupdate_hide_item1').slideToggle();
            if (teams == "Read more") {

                $(".readmore_button1").html("Read less");
            } else {
                $(".readmore_button1").html("Read more");
            }
        })
        $('.readmore_button_2').click(function() {
            var teams2 = $(".readmore_button_2").html();
            $('.campusupdate_hide_item_2').slideToggle();
            if (teams2 == "Read more") {
                console.log(teams2)
                $(".readmore_button_2").html("Read less");
            } else {
                $(".readmore_button_2").html("Read more");
            }
        })
        $('.readmore_button3').click(function() {
            var teams = $(".readmore_button3").html();
            $('.campusupdate_hide_item3').slideToggle();
            if (teams == "Read more") {

                $(".readmore_button3").html("Read less");
            } else {
                $(".readmore_button3").html("Read more");
            }
        })
        $('.readmore_button4').click(function() {
            var teams = $(".readmore_button4").html();
            $('.campusupdate_hide_item5').slideToggle();
            if (teams == "Read more") {

                $(".readmore_button4").html("Read less");
            } else {
                $(".readmore_button4").html("Read more");
            }
        })
        $('.readmore_button5').click(function() {
            var teams = $(".readmore_button4").html();
            $('.campusupdate_hide_item4').slideToggle();
            if (teams == "Read more") {

                $(".readmore_button5").html("Read less");
            } else {
                $(".readmore_button5").html("Read more");
            }
        })

        $('.campuscarousel').owlCarousel({
            loop: true,
            nav: false,
            dots: true,
            items: 1,
            autoplay: true,
            autoplayTimeout: 5000,
            autoplayHoverPause: true
        })



        tinymce.init({
            selector: '#mytextarea'
        });



        $('#btns').click(function() {
            $('.mobile_nav').toggleClass('mobile_nav_active');
            $(this).find('i').toggleClass('bi bi-list bi bi-x')
        })

        $(window).scroll(function() {
            $('.main_nav').toggleClass('main_nav_scrolled', $(this).scrollTop() > 200)
            $('.small_nav').toggleClass('navboxs_scrolled', $(this).scrollTop() > 200)
            $('.sub_nav').toggleClass('subnav_scrolled', $(this).scrollTop() > 200).slideDown();
            // $('.mobile_nav').toggleClass('scrolled', $(this).scrollTop() > 200)
        })

        const currentLocation = location.href;
        const menuItem = document.querySelectorAll('.navItem');
        const menuLength = menuItem.length;

        for (let i = 0; i < menuLength; i++) {
            if (menuItem[i].href === currentLocation) {
                menuItem[i].className = "active"
            }
        }

        const nextIcon = '<i class="bi bi-arrow-right"></i>';
        const prevIcon = '<i class="bi bi-arrow-left"></i>';


        // section-3 slider
        var swiper = new Swiper('#section3_slider', {
            // slidesPerView: 4,
            spaceBetween: 30,
            loop: true,
            pagination: {
                el: '.swiper-pagination',
                clickable: true,
            },
            // navigation: {
            //     nextEl: '.swiper-button-next-unique',
            //     prevEl: '.swiper-button-prev-unique'
            // },
            // autoplay: {
            //     delay: 2500,
            //     disableOnInteraction: true,
            // },
            navigation: {
                nextEl: '.swiper-button-next',
                prevEl: '.swiper-button-prev',
            },
            breakpoints: {
                500: {
                    slidesPerView: 1,
                    spaceBetween: 30,
                    pagination: false,

                },
                768: {
                    slidesPerView: 2,
                    spaceBetween: 10,
                },
                1024: {
                    slidesPerView: 4,
                    spaceBetween: 10,
                    nav: false,
                },
            }
        });
        // capmus update slider
        var swiper = new Swiper('#campusupdates', {
            slidesPerView: 1,
            spaceBetween: 10,
            loop: true,

            // autoplay: {
            //     delay: 2500,
            //     disableOnInteraction: false,
            // },
            pagination: {
                el: '.swiper-paginations',
                clickable: true,
            },
            navigation: {
                nextEl: '.swiper-button-next',
                prevEl: '.swiper-button-prev'
            },
            breakpoints: {
                640: {
                    slidesPerView: 1,
                    spaceBetween: 10,
                },
                768: {
                    slidesPerView: 2,
                    spaceBetween: 20,
                },
                1024: {
                    slidesPerView: 3,
                    spaceBetween: 20,
                    loop: true,
                },
            }
        });
        $('.count').each(function() {
            $(this).prop('Counter', 0).animate({
                Counter: $(this).text()
            }, {
                duration: 6000,
                easing: 'swing',
                step: function(now) {
                    $(this).text(Math.ceil(now));
                }
            });
        });
       

    });
</script>

